// WRONG ANSWER (maybe because long paths in the early stages may become as short as shorter paths in the end?).
/*
 * Note: The "shorter path" constriction must apply to all nodes in the path.
 */
class Solution2 {
public:
    int countRestrictedPaths(const int n, const std::vector<std::vector<int>>& edges) {
        if (n == 1) {
            return 0;
        }

        // Create graph.
        auto graph = std::unordered_map<int, std::unordered_map<int, int>>();
        for (const auto& edge: edges) {
            const int node1 = edge[0] - 1;    // Note that `edge` is 1-indexed.
            const int node2 = edge[1] - 1;

            graph[node1][node2] = edge[2];
            graph[node2][node1] = edge[2];
        }

        // Dijkstra from end.
        auto distances = std::vector<int>(n, INT_MAX);
        distances[n - 1] = 0;

        auto cmp = [&distances] (const int& lhs, const int& rhs) -> bool {
            if (distances[lhs] == distances[rhs]) {
                // Avoid equal cases. Otherwise, they'll be ignored by the set.
                return lhs < rhs;
            } else {
                return distances[lhs] < distances[rhs];
            }
        };
        auto unvisitedNodes = std::set<int, decltype(cmp)>(cmp);

        for (int i = 0; i < n; i += 1) {
            unvisitedNodes.insert(i);
        }

        auto paths = std::vector<int>(n, 0);    // Handle different paths with the same distance.
        paths[n - 1] = 1;

        while (!unvisitedNodes.empty()) {
            auto currentNode = *unvisitedNodes.begin();
            unvisitedNodes.erase(unvisitedNodes.begin());    // Mark current node as visited.

            const auto neighborDistances = graph[currentNode];
            for (const auto& [neighbor, currentDistance]: neighborDistances) {
                const int newDistance = currentDistance + distances[currentNode];

                if (newDistance == distances[neighbor]) {
                    paths[neighbor] += paths[currentNode];
                } else if (newDistance < distances[neighbor]) {
                    paths[neighbor] = paths[currentNode];

                    unvisitedNodes.erase(neighbor);
                    distances[neighbor] = newDistance;
                    unvisitedNodes.insert(neighbor);
                }
            }
        }

        // Loop over start's neighbors.
        int returnValue = 0;

        // It's guaranteed to have at least 1 neighbor.
        const auto& startNeighbors = graph[0];
        const auto& startDistance = distances[0];
        for (const auto& [neighbor, _]: startNeighbors) {
            if (startDistance > distances[neighbor]) {
                returnValue += paths[neighbor];
            }
        }

        return returnValue;
    }
};


#pragma mark - 3. Dijkstra + DFS/DP (revisit)
// Looks counter-intuitive and low performance.
class Solution {
private:
    static constexpr int MOD = 1000000007;

private:
    int dfs(std::unordered_map<int, std::unordered_map<int, int>>& graph, std::vector<int>& distances, std::vector<int>& dp, const int currentNode) {
        if (currentNode == 0) {
            // This is the first node.
            return 1;
        }

        if (dp[currentNode] > 0) {
            // This node's solution count has been calculated.
            return dp[currentNode];
        }

        int returnValue = 0;

        const int& currentDistance = distances[currentNode];

        const auto& neighbors = graph[currentNode];    // All nodes are guaranteed to have neighbors.
        for (const auto& [neighbor, _]: neighbors) {
            const int& neighborDistance = distances[neighbor];

            if (neighborDistance > currentDistance) {
                // This neighbor is qualified.
                int neighborResult = dfs(graph, distances, dp, neighbor);
                neighborResult %= MOD;

                returnValue += neighborResult;
                returnValue %= MOD;
            }
        }

        returnValue %= MOD;
        dp[currentNode] = returnValue;

        return returnValue;
    }

public:
    int countRestrictedPaths(const int n, const std::vector<std::vector<int>>& edges) {
        if (n == 1) {
            return 0;
        }

        // Create graph.
        auto graph = std::unordered_map<int, std::unordered_map<int, int>>();
        for (const auto& edge: edges) {
            const int node1 = edge[0] - 1;    // Note that `edge` is 1-indexed.
            const int node2 = edge[1] - 1;

            graph[node1][node2] = edge[2];
            graph[node2][node1] = edge[2];
        }

        // Dijkstra from end.
        auto distances = std::vector<int>(n, INT_MAX);
        distances[n - 1] = 0;

        auto cmp = [&distances] (const int& lhs, const int& rhs) -> bool {
            if (distances[lhs] == distances[rhs]) {
                // Avoid equal cases. Otherwise, they'll be ignored by the set.
                return lhs < rhs;
            } else {
                return distances[lhs] < distances[rhs];
            }
        };
        auto unvisitedNodes = std::set<int, decltype(cmp)>(cmp);

        for (int i = 0; i < n; i += 1) {
            unvisitedNodes.insert(i);
        }

        while (!unvisitedNodes.empty()) {
            auto currentNode = *unvisitedNodes.begin();
            unvisitedNodes.erase(unvisitedNodes.begin());    // Mark current node as visited.

            const auto neighborDistances = graph[currentNode];
            for (const auto& [neighbor, currentDistance]: neighborDistances) {
                const int newDistance = currentDistance + distances[currentNode];

                if (newDistance < distances[neighbor]) {
                    unvisitedNodes.erase(neighbor);
                    distances[neighbor] = newDistance;
                    unvisitedNodes.insert(neighbor);
                }
            }
        }

        // DFS from start: the current node must have a shorter distance than the previous node.
        auto dp = std::vector<int>(n, 0);
        int returnValue = dfs(graph, distances, dp, n - 1);

        return returnValue;
    }
};



void test(const int n, const std::vector<std::vector<int>>& edges, const int expectedResult) {
    auto solutionInstance = Solution();

    auto result = solutionInstance.countRestrictedPaths(n, edges);

    if (result == expectedResult) {
        std::cout << terminal_format::OK_GREEN << "[Correct] " << terminal_format::ENDC << n << " " << edges << ": " << result << std::endl;
    } else {
        std::cout << terminal_format::FAIL << terminal_format::BOLD << "[Wrong] " << terminal_format::ENDC << n << " " << edges << ": " << result << " (should be " << expectedResult << ")" << std::endl;
    }
}


