/*
 * 334. Increasing Triplet Subsequence
 * https://leetcode.com/problems/increasing-triplet-subsequence/
 */

#include <iostream>
#include <vector>

#include "helpers/Operators.hpp"


#pragma mark - 1. DP like solution
// Runtime: 8 ms, faster than 73.67% of C++ online submissions for Increasing Triplet Subsequence.
// Memory Usage: 10.2 MB, less than 85.66% of C++ online submissions for Increasing Triplet Subsequence.
class Solution {
public:
    bool increasingTriplet(std::vector<int>& nums) {
        if (nums.size() < 3) {
            return false;
        }

        int* minNum0 = nullptr;
        int* minNum1 = nullptr;

        for (const auto& num: nums) {
            if (minNum0 == nullptr) {
                // This is the 0th number.
                minNum0 = new int(num);
            } else if (num <= *minNum0) {
                // Smaller than min num 0.
                // NOTE: The equal case is captured here so that the new number does not contaminate `minNum1` in subsequent cases.
                *minNum0 = num;
            } else if (minNum1 == nullptr) {
                // Min num 1 is not initialized.
                // NOTE: Here we must make sure that `num > *minNum0` (cannot equal).
                minNum1 = new int(num);
            } else if (num < *minNum1) {
                // Smaller than min num 1.
                // NOTE: Here we must make sure that `num > *minNum0` (cannot equal).
                *minNum1 = num;
            } else if (num > *minNum1) {
                // Sequence found.
                return true;
            }
        }

        return false;
    }
};


void test(const std::vector<int>& nums, const bool expectedResult) {
    static auto solutionInstance = Solution();

    auto numsCopy = nums;
    const auto result = solutionInstance.increasingTriplet(numsCopy);

    if (result == expectedResult) {
        std::cout << "[Correct] " << nums << ": " << result << std::endl;
    } else {
        std::cout << "[Wrong] " << nums << ": " << result << " (should be " << expectedResult << ")" << std::endl;
    }
}


